mapper.select:
SELECT id, event_id, device_id, user_id, event_type, environment, topic, payload,
       app_version, os, os_version, device_model, network_type, session_id, is_critical,
       received_at, event_timestamp, created_at, updated_at
FROM analytics_event


        AND user_id = #{userId}


        AND device_id = #{deviceId}


        AND event_type = #{eventType}


        AND environment = #{environment}


        AND is_critical = #{isCritical}


        AND created_at >= #{startTime}


        AND created_at <= #{endTime}


ORDER BY received_at DESC

    LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM analytics_event


        AND user_id = #{userId}


        AND device_id = #{deviceId}


        AND event_type = #{eventType}


        AND environment = #{environment}


        AND is_critical = #{isCritical}


        AND created_at >= #{startTime}


        AND created_at <= #{endTime}

mapper.insert:
INSERT INTO analytics_event (
    event_id, device_id, user_id, event_type, environment, topic, payload,
    app_version, os, os_version, device_model, network_type, session_id, is_critical,
    received_at, event_timestamp, created_at, updated_at
) VALUES (
    #{eventId}, #{deviceId}, #{userId}, #{eventType}, #{environment}, #{topic}, #{payload},
    #{appVersion}, #{os}, #{osVersion}, #{deviceModel}, #{networkType}, #{sessionId}, #{isCritical},
    #{receivedAt}, #{eventTimestamp}, #{createdAt}, #{updatedAt}
)
