mapper.select:
SELECT id, email, password, nickname, status, created_at, updated_at
FROM customer
WHERE email = #{email}

SELECT id, email, nickname, status, created_at, updated_at FROM customer
AND (email LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}
ORDER BY id DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM customer
AND (email LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}

mapper.update:
UPDATE customer
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}
