mapper.insert:
INSERT INTO loan_application_otp (
    application_id, otp_token, otp_code_hash, expires_at,
    verify_attempts, verified, verified_at, created_at
) VALUES (
    #{applicationId}, #{otpToken}, #{otpCodeHash}, #{expiresAt},
    #{verifyAttempts}, #{verified}, #{verifiedAt}, #{createdAt}
)

mapper.update:
UPDATE loan_application_otp
SET verify_attempts = verify_attempts + 1,
    updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE loan_application_otp
SET verified = 1,
    verified_at = #{verifiedAt},
    updated_at = #{updatedAt}
WHERE id = #{id}
