mapper.insert:
INSERT INTO loan_contract_document (
    application_id, contract_no, template_version, contract_content,
    digest, status, signed_at, created_at
) VALUES (
    #{applicationId}, #{contractNo}, #{templateVersion}, #{contractContent},
    #{digest}, #{status}, #{signedAt}, #{createdAt}
)

mapper.update:
UPDATE loan_contract_document
SET status = #{status},
    signed_at = #{signedAt},
    updated_at = #{updatedAt}
WHERE id = #{id}
