mapper.select:
SELECT DISTINCT p.* FROM sys_permission p INNER JOIN sys_role_permission rp ON p.id = rp.permission_id WHERE rp.role_id IN
#{roleId}
AND p.status = 1 ORDER BY p.resource_type ASC, p.permission_code ASC

SELECT * FROM sys_permission WHERE id IN
#{id}
ORDER BY permission_code ASC

mapper.update:
UPDATE sys_permission
SET permission_name = #{permissionName},
    resource_type = #{resourceType},
    description = #{description},
    status = #{status}
WHERE id = #{id}
